import React from 'react'

import './App.css';
import { TodoCounter } from './pages/TodoCounter.js';
import { TodoSearch } from './pages/TodoSearch.js';
import { TodoList } from './pages/TodoList.js';
import { TodoItem } from './pages/TodoItem.js';
import { CreateTodoButton } from './pages/CreateTodoButton.js';

const defaultTodos = [
  { text: 'navegar en e621', completed: true },
  { text: 'Decirle adios a dios', completed: false },
  { text: 'llorar en la llorería', completed: false },
  { text: 'molestar a calamardo', completed: false }
]

function App() {
  const [todos, setTodos] = React.useState(defaultTodos);
  const [searchValue, setSearchValue] = React.useState('');

  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;

  let searchedTodos = [];

  if (!searchValue.length >= 1) {
    searchedTodos = todos;
  }
  else {
    searchedTodos = todos.filter(todo => {
      const todoText = todo.text.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return todoText.includes(searchText);
    });
  }


  const completeTodo = (text) => {
    // const todoIndex = todos.findIndex(todo => todo.text === text);

    // const newTodos = [...todos];
    // newTodos[todoIndex].completed = true;
    // setTodos(newTodos);
    setTodos(todos.map((todo) => {
      if (todo.text === text) return  {...todos, completed: !todo.completed}
      return todo;
    }))
  }

  const deleteTodo = (text) => {
    const todoIndex = todos.findIndex(todo => todo.text === text);

    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    setTodos(newTodos);
  }

  return (
    <>
      <TodoCounter
        total={totalTodos}
        completed={completedTodos}
      />
      <TodoSearch
        searchValue={searchValue}
        setSearchValue={setSearchValue}
      />
      <TodoList>
        {searchedTodos.map(todo => (
          <TodoItem
            key={todo.text}
            text={todo.text}
            completed={todo.completed}
            onComplete={() => completeTodo(todo.text)}
            onDelete={() => deleteTodo(todo.text)}
          />
        ))}
      </TodoList>
      <CreateTodoButton />
    </>
  );
}

export default App;
