import React from "react";
import { TodoContext } from "../components/TodoContext/";
import { TodoCounter } from '../components/TodoCounter/';
import { TodoSearch } from '../components/TodoSearch/';
import { TodoList } from '../components/TodoList/';
import { TodoItem } from '../components/TodoItem/';
import { CreateTodoButton } from '../components/CreateTodoButton/';
import { Modal } from '../components/Modal';
import { TodoForm } from '../components/TodoForm'
import './App.css'
function AppUI() {
    const { error,
        loading,
        searchedTodos,
        toggleCompleteTodo,
        deleteTodo,
        openModal,
        setOpenModal,
    } = React.useContext(TodoContext);
    return (
        <>
            <TodoCounter />
            <TodoSearch />
            <TodoList>
                {error && <p className="aditionalText">for fuck's sake, there was an error</p>}
                {loading && <p className="aditionalText">Loading, please wait. . .</p>}
                {(!loading && !searchedTodos.length) && <p className="aditionalText">Nobody here but us chickens!</p>}

                {searchedTodos.map(todo => (
                    <TodoItem
                        key={todo.text}
                        text={todo.text}
                        completed={todo.completed}
                        onComplete={() => toggleCompleteTodo(todo.text)}
                        onDelete={() => deleteTodo(todo.text)}
                    />
                ))}
            </TodoList>

            {!!openModal && (
                <Modal>
                    <TodoForm/>
                </Modal>
            )}

            <CreateTodoButton
                setOpenModal={setOpenModal}
                openModal={openModal}
            />
        </>
    );
}
export { AppUI }
