import React from "react";
import { TodoContext } from "../TodoContext/index.js";
import { TodoCounter } from '../components/TodoCounter/TodoCounter.js';
import { TodoSearch } from '../components/TodoSearch/TodoSearch.js';
import { TodoList } from '../components/TodoList/TodoList.js';
import { TodoItem } from '../components/TodoItem/TodoItem.js';
import { CreateTodoButton } from '../components/CreateTodoButton/CreateTodoButton.js';

function AppUI() {
    return (
        <>
            <TodoCounter />
            <TodoSearch />

            <TodoContext.Consumer>
                {({ error,
                    loading,
                    searchedTodos,
                    toggleCompleteTodo,
                    deleteTodo
                }) => (<TodoList>
                    {error && <p>for fuck's sake, there was an error</p>}
                    {loading && <p>Loading, please wait. . .</p>}
                    {(!loading && !searchedTodos.length) && <p>Nobody here but us chickens!</p>}

                    {searchedTodos.map(todo => (
                        <TodoItem
                            key={todo.text}
                            text={todo.text}
                            completed={todo.completed}
                            onComplete={() => toggleCompleteTodo(todo.text)}
                            onDelete={() => deleteTodo(todo.text)}
                        />
                    ))}
                </TodoList>)}
            </TodoContext.Consumer>

            <CreateTodoButton />
        </>
    );
}
export { AppUI }
