import React from 'react'

import { AppUI } from './AppUI';

const defaultTodos = [
  { text: 'navegar en e621', completed: true },
  { text: 'Decirle adios a dios', completed: false },
  { text: 'molestar a calamardo', completed: true },
  { text: 'llorar en la llorería', completed: false },
]

function useLocalStorage(itemName, initialValue) {
  React.useEffect(() => {
    setTimeout(() => {
      const localStorageItem = localStorage.getItem(itemName);
      let parsedItem;

      if (!localStorageItem) {
        localStorage.setItem(itemName, JSON.stringify(initialValue))
        parsedItem = initialValue
      } else {
        parsedItem = JSON.parse(localStorageItem)
      }

    }, 1000);
  });


  const [item, setItem] = React.useState(parsedItem);

  const saveItem = (newItem) => {
    const stringifiedTodos = JSON.stringify(newItem);
    localStorage.setItem(itemName, stringifiedTodos);
    setItem(newItem)
  };

  return [
    item,
    saveItem
  ];

}

function App() {
  const [todos, saveTodos] = useLocalStorage('TODO_V1', []);
  const [searchValue, setSearchValue] = React.useState('');



  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;

  let searchedTodos = [];

  if (!searchValue.length >= 1) {
    searchedTodos = todos;
  }
  else {
    searchedTodos = todos.filter(todo => {
      const todoText = todo.text.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return todoText.includes(searchText);
    });
  }


  const toggleCompleteTodo = (text) => {
    // const todoIndex = todos.findIndex(todo => todo.text === text);

    // const newTodos = [...todos];
    // newTodos[todoIndex].completed = true;
    // setTodos(newTodos);
    saveTodos(todos.map((todo) => {
      if (todo.text === text) return { ...todo, completed: !todo.completed };
      return todo;
    }))
  }

  const deleteTodo = (text) => {
    const todoIndex = todos.findIndex(todo => todo.text === text);

    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  };

  // console.log('Render BEFORE use effect');
  // React.useEffect(() => {
  //   console.log('use effect')
  // }, [totalTodos]);
  // console.log('Render AFTER use effect');

  return (
    <AppUI
      totalTodos={totalTodos}
      completedTodos={completedTodos}
      searchValue={searchValue}
      setSearchValue={setSearchValue}
      searchedTodos={searchedTodos}
      toggleCompleteTodo={toggleCompleteTodo}
      deleteTodo={deleteTodo}
    />
  );
}

export default App;
