import React from "react";
import { TodoCounter } from '../pages/TodoCounter/TodoCounter.js';
import { TodoSearch } from '../pages/TodoSearch/TodoSearch.js';
import { TodoList } from '../pages/TodoList/TodoList.js';
import { TodoItem } from '../pages/TodoItem/TodoItem.js';
import { CreateTodoButton } from '../pages/CreateTodoButton/CreateTodoButton.js';

function AppUI({
    totalTodos,
    completedTodos,
    searchValue,
    setSearchValue,
    searchedTodos,
    toggleCompleteTodo,
    deleteTodo,
}) {
    return (
        <>
            <TodoCounter
                total={totalTodos}
                completed={completedTodos}
            />
            <TodoSearch
                searchValue={searchValue}
                setSearchValue={setSearchValue}
            />
            <TodoList>
                {error && <p>for fuck's sake, there was an error</p>}
                {loading && <p>Loading, please wait. . .</p>}
                {(!loading && !searchedTodos.length) && <p>Create your firts little silly task</p>}

                {searchedTodos.map(todo => (
                    <TodoItem
                        key={todo.text}
                        text={todo.text}
                        completed={todo.completed}
                        onComplete={() => toggleCompleteTodo(todo.text)}
                        onDelete={() => deleteTodo(todo.text)}
                    />
                ))}
            </TodoList>
            <CreateTodoButton />
        </>
    );
}
export { AppUI }
